// Funci√≥n que calcula los minutos totales de los reactivos

// Primero guardar valor de play
// para saber que ya inici√≥ el ciclo
flow.set("play", true);
// iniciar con el primer reactivo (zero)
flow.set("num_reactivo", 0);

// Crear un array de los minutos totales para cada reactivo
let mt = [0, 0, 0, 0, 0, 0];
// leer arrays
let h = flow.get("horas");
let m = flow.get("minutos");

// Convertir horas a minutos y sumar a minutos totales
for (let i = 0; i < 6; i++) {
    if (isNaN(h[i])) h[i] = 0;
    if (isNaN(m[i])) m[i] = 0;
    // sumar horas y minutos de cada reactivo
    mt[i] = h[i]*60 + m[i];
}

// guardar valores calculados en variable del flujo
flow.set("minutos_totales", mt);
//msg.payload = mt;
return {payload:0}; //fin
//return minutos_totales;

// Programa principal
// Tiene 4 salidas
// 1. Debug (depuraci√≥n)
// 2. Gcode
// 3. Loop o espera
// 4. Fin del programa

// Este programa revisa el n√∫mero de reactivo y los minutos asignados a ese reactivo
// Controla los movimientos de la m√°quina conforme a los valores de tiempo configurados
// Este nodo tiene 3 salidas: debug, gcode y final

// Obtener variable para saber en qu√© paso vamos
let paso = context.get("paso") || 1;
// obtener variable para saber en qu√© reactivo vamos
let n = flow.get("num_reactivo");
// status
let status = flow.get("status");
// variable z
let z = 0;

switch (paso) 
{
    case 1: //paso 1
        // Leer variables del flujo
        let m = flow.get("minutos_totales");
        let minutos = 0; //minutos del reactivo actual

        // checar numero de reactivo
        while (n < 6) {
            // obtener minutos para el reactivo actual
            minutos = m[n];
            msg.payload = "reactivo " + n + ": " + minutos + " minutos";
            node.send([msg, null, null, null]);

            // si el valor de minutos es mayor a cero
            if (minutos > 0) {
                // esto inicia el movimiento a las coordenadas del recipiente
                context.set("paso", 2); //vamos al paso 2
                msg.payload = 0;
                return [null, null, msg, null];
            }

            // incrementar numero de reactivo
            flow.set("num_reactivo", ++n);
        }
        // cuando termina de revisar los reactivos, termina el programa
        context.set("paso", 8); //vamos al final
        msg.payload = 0;
        return [null, null, msg, null];
        break;

    case 2: //paso 2
        // Env√≠ar gcode para mover la m√°quina a las coordenadas de los recipientes
        // array de coordenadas de los recipientes
        const x = [-400, -400, -400, -400, -400,  -400];
        const y = [ -10, -250, -490, -730, -970, -1210];

        // gcode para mover a las coordenadas indicadas
        msg.payload = "G1G21G90X" + x[n] + "Y" + y[n] + "F600\n";
        node.send([null, msg, null, null]);

        // ahora esperar a que la m√°quina llegue a las coordenadas
        context.set("paso", 3); //vamos al paso 3
        msg.payload = 0;
        return [null, null, msg, null];
        break;

    case 3: //paso 3
        // esperar a que la m√°quina llegue a las coordenadas
        // ver valor del status
        status = flow.get("status");
        // comparar con la palabra Idle
        if (status == "<Idle") {
            // ahora vamos a iniciar la agitaci√≥n
            context.set("paso", 4); //vamos al paso 4
            msg.payload = 0;
            return [null, null, msg, null];
        }
        else {
            msg.payload = 0;
            return [null, null, msg, null];
        }
        break;

    case 4: //paso 4
        // gcode para bajar la canastilla
        z = -85;
        msg.payload = "G1G21G90Z" + z + "F600\n";
        node.send([null, msg, null, null]);

        // ahora esperar a que la canastilla baje
        context.set("paso", 5); //vamos al paso 5
        msg.payload = 0;
        return [null, null, msg, null];
        break;

    case 5: //paso 5
        // esperar a que la canastilla baje
        // ver valor del status
        status = flow.get("status");
        // comparar con la palabra Idle
        if (status == "<Idle") {
            // ahora vamos a subir
            context.set("paso", 6); //vamos al paso 6
            msg.payload = 0;
            return [null, null, msg, null];
        }
        else {
            msg.payload = 0;
            return [null, null, msg, null];
        }
        break;

    case 6: //paso 6
        // gcode para subir la canastilla
        z = -10;
        msg.payload = "G1G21G90Z" + z + "F600\n";
        node.send([null, msg, null, null]);

        // ahora esperar a que la canastilla suba
        context.set("paso", 7); //vamos al paso 7
        msg.payload = 0;
        return [null, null, msg, null];
        break;

    case 7: //paso 7
        // esperar a que la canastilla suba
        // ver valor del status
        status = flow.get("status");
        // comparar con la palabra Idle
        if (status == "<Idle") {
            // ahora vamos a subir
            context.set("paso", 1); //regresar
            // incrementar numero de reactivo
            flow.set("num_reactivo", ++n);
            msg.payload = 0;
            return [null, null, msg, null];
        }
        else {
            msg.payload = 0;
            return [null, null, msg, null];
        }
        break;

    case 8: //paso 8
        // fin del programa (por ahora)
        context.set("paso", 1); //reiniciar el n√∫mero de paso
        flow.set("num_reactivo", 0); //reiniciar el n√∫mero de reactivo
        msg.payload = 0;
        return [null, null, null, msg];
        break;
}

// Esta funci√≥n separa el reporte de status:
// extrae el estado y las coordenadas

// string
let s = msg.payload;

// identificar si es un reporte de status
if (s.charAt(0) == "<") {
    // separar string
    let words = s.split("|")
    // el primer elemento es el status
    let status = words[0];
    flow.set("status", status);
    // buscar la string que contiene las coordenadas
    words.forEach(function(element) {
        let inicio = element.substring(0, 4);
        if (inicio == "Mpos") {
            // separar la cadena en los "dos puntos"
            s = element.split(":");
            // separar la cadena en las "comas"
            s = s.split(",");
            // guardar array de n√∫meros en una variable del flujo
            flow.set("mpos", s);
        }
    });
}

return msg;

